import React, { useEffect, useMemo, useRef, useState, useImperativeHandle, forwardRef } from "react";

const SwipeCard = forwardRef(function SwipeCard({ id, onSwipe, onCardLeftScreen, disabled = false, style = {}, className = "", threshold = 120, children }, ref) {
  const elRef = useRef(null);
  const startRef = useRef({ x: 0, y: 0 });
  const draggingRef = useRef(false);
  const [offset, setOffset] = useState({ x: 0, y: 0 });
  const [rot, setRot] = useState(0);
  const [isExiting, setIsExiting] = useState(false);

  useImperativeHandle(ref, () => ({
    swipe(direction = "right") {
      if (isExiting) return;
      const sign = direction === "right" ? 1 : -1;
      setIsExiting(true);
      setOffset({ x: sign * (window.innerWidth || 1000), y: -100 });
      setRot(sign * 30);
      setTimeout(() => {
        onSwipe && onSwipe(direction);
        onCardLeftScreen && onCardLeftScreen();
      }, 300);
    }
  }), [isExiting, onSwipe, onCardLeftScreen]);

  useEffect(() => {
    const el = elRef.current;
    if (!el) return;

    function onPointerDown(e) {
      if (disabled || isExiting) return;
      try {
        el.setPointerCapture(e.pointerId);
      } catch (_) {}
      draggingRef.current = true;
      startRef.current = { x: e.clientX, y: e.clientY };
      el.style.transition = "none";
    }

    function onPointerMove(e) {
      if (!draggingRef.current) return;
      const dx = e.clientX - startRef.current.x;
      const dy = e.clientY - startRef.current.y;
      setOffset({ x: dx, y: dy });
      setRot(Math.max(Math.min(dx / 20, 40), -40));
    }

    function onPointerUp(e) {
      if (!draggingRef.current) return;
      draggingRef.current = false;
      try {
        el.releasePointerCapture(e.pointerId);
      } catch (_) {}
      const dx = e.clientX - startRef.current.x;
      const dy = e.clientY - startRef.current.y;
      if (dx > threshold) {
        setIsExiting(true);
        setOffset({ x: (window.innerWidth || 1000), y: dy });
        setRot(30);
        el.style.transition = "transform 300ms ease-out";
        setTimeout(() => {
          onSwipe && onSwipe("right");
          onCardLeftScreen && onCardLeftScreen();
        }, 300);
      } else if (dx < -threshold) {
        setIsExiting(true);
        setOffset({ x: -(window.innerWidth || 1000), y: dy });
        setRot(-30);
        el.style.transition = "transform 200ms ease-out";
        setTimeout(() => {
          onSwipe && onSwipe("left");
          onCardLeftScreen && onCardLeftScreen();
        }, 300);
      } else {
        el.style.transition = "transform 200ms ease-out";
        setOffset({ x: 0, y: 0 });
        setRot(0);
      }
    }

    el.addEventListener("pointerdown", onPointerDown);
    window.addEventListener("pointermove", onPointerMove);
    window.addEventListener("pointerup", onPointerUp);

    return () => {
      el.removeEventListener("pointerdown", onPointerDown);
      window.removeEventListener("pointermove", onPointerMove);
      window.removeEventListener("pointerup", onPointerUp);
    };
  }, [disabled, isExiting, onSwipe, onCardLeftScreen, threshold]);

  const transform = `translate(${offset.x}px, ${offset.y}px) rotate(${rot}deg)`;
  const baseStyle = {
    transform,
    touchAction: "none",
    cursor: disabled ? "default" : "grab",
    willChange: "transform",
    ...style
  };

  return (
    <div
      ref={elRef}
      style={baseStyle}
      className={className}
      aria-hidden={isExiting}
    >
      <div style={{ position: "absolute", top: 8, left: 8, pointerEvents: "none", fontSize: 12, opacity: Math.min(Math.abs(offset.x) / 200, 1) }}>
        {offset.x > 0 ? "LIKE →" : offset.x < 0 ? "← NOPE" : ""}
      </div>
      {typeof children === "function" ? children({ offset, rot }) : children}
    </div>
  );
});

export default function SwipeFlicks() {
  const STORAGE_KEY = "swipeflicks_state_v1";

  const sampleMovies = [
    { id: "1", title: "The Grand Budapest Hotel", poster: "https://image.tmdb.org/t/p/w500/1.jpg" },
    { id: "2", title: "Inception", poster: "https://image.tmdb.org/t/p/w500/2.jpg" },
    { id: "3", title: "Parasite", poster: "https://image.tmdb.org/t/p/w500/3.jpg" },
    { id: "4", title: "Spirited Away", poster: "https://image.tmdb.org/t/p/w500/4.jpg" },
    { id: "5", title: "La La Land", poster: "https://image.tmdb.org/t/p/w500/5.jpg" }
  ];

  const loadState = () => {
    try {
      const raw = localStorage.getItem(STORAGE_KEY);
      if (!raw) return { movies: sampleMovies, votes: { userA: [], userB: [] } };
      const parsed = JSON.parse(raw);
      return {
        movies: parsed.movies && parsed.movies.length ? parsed.movies : sampleMovies,
        votes: parsed.votes || { userA: [], userB: [] }
      };
    } catch (e) {
      console.warn("Failed to load state, using defaults.", e);
      return { movies: sampleMovies, votes: { userA: [], userB: [] } };
    }
  };

  const initial = loadState();
  const [movies, setMovies] = useState(initial.movies);
  const [votes, setVotes] = useState(initial.votes);
  const [currentUser, setCurrentUser] = useState("userA");
  const [lastDirection, setLastDirection] = useState(null);
  const [newTitle, setNewTitle] = useState("");
  const [newPoster, setNewPoster] = useState("");
  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    const payload = { movies, votes };
    try {
      localStorage.setItem(STORAGE_KEY, JSON.stringify(payload));
    } catch (e) {
      console.warn("Failed to save state.", e);
    }
  }, [movies, votes]);

  // Reset current index when user changes so they see all movies
  useEffect(() => {
    setCurrentIndex(0);
  }, [currentUser]);

  const matches = useMemo(() => {
    const a = new Set(votes.userA || []);
    const b = new Set(votes.userB || []);
    const common = [...a].filter((id) => b.has(id));
    return movies.filter((m) => common.includes(m.id));
  }, [votes, movies]);

  function handleSwipe(direction, movieId) {
    setLastDirection(direction + " (" + movieId + ")");
    if (direction === "right") {
      setVotes((prev) => {
        const copy = { userA: [...(prev.userA || [])], userB: [...(prev.userB || [])] };
        if (!copy[currentUser].includes(movieId)) copy[currentUser].push(movieId);
        return copy;
      });
    } else if (direction === "left") {
      setVotes((prev) => {
        const copy = { userA: [...(prev.userA || [])], userB: [...(prev.userB || [])] };
        copy[currentUser] = copy[currentUser].filter((id) => id !== movieId);
        return copy;
      });
    }
  }

  function addMovie(e) {
    e.preventDefault();
    if (!newTitle.trim()) return;
    const id = Date.now().toString();
    const movie = { id, title: newTitle.trim(), poster: newPoster.trim() };
    setMovies((m) => [movie, ...m]);
    setCurrentIndex(0);
    setNewTitle("");
    setNewPoster("");
  }

  function resetAll() {
    if (!window.confirm("Reset all movies and votes?")) return;
    setMovies(sampleMovies);
    setVotes({ userA: [], userB: [] });
    setCurrentIndex(0);
    try { localStorage.removeItem(STORAGE_KEY); } catch (e) {}
  }

  function exportJSON() {
    const blob = new Blob([JSON.stringify({ movies, votes }, null, 2)], { type: "application/json" });
    const url = URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = "swipeflicks_export.json";
    a.click();
    URL.revokeObjectURL(url);
  }

  function importJSON(e) {
    const file = e.target.files[0];
    if (!file) return;
    const reader = new FileReader();
    reader.onload = (ev) => {
      try {
        const parsed = JSON.parse(ev.target.result);
        if (parsed.movies) setMovies(parsed.movies);
        if (parsed.votes) setVotes(parsed.votes);
        setCurrentIndex(0);
        alert("Imported!");
      } catch (err) {
        alert("Invalid JSON file");
      }
    };
    reader.readAsText(file);
  }

  const remaining = movies.slice(currentIndex);

  function onCardSwiped(direction, movieId) {
    handleSwipe(direction, movieId);
    setTimeout(() => setCurrentIndex((ci) => Math.min(ci + 1, movies.length)), 50);
  }

  function programmaticSwipe(direction) {
    if (remaining.length === 0) return;
    const top = remaining[0];
    handleSwipe(direction, top.id);
    setCurrentIndex((ci) => Math.min(ci + 1, movies.length));
  }

  return (
    <div className="min-h-screen bg-gray-50 p-6 flex flex-col items-center">
      <div className="w-full max-w-3xl">
        <header className="flex items-center justify-between mb-4">
          <h1 className="text-2xl font-bold">SwipeFlicks</h1>
          <div className="text-sm text-gray-600">Two-person movie picker • Local-only</div>
        </header>

        <section className="mb-4 flex gap-2 items-center">
          <div className="flex items-center gap-2">
            <label className="text-sm">Current:</label>
            <button
              onClick={() => setCurrentUser("userA")}
              className={`px-3 py-1 rounded ${currentUser === "userA" ? "bg-blue-600 text-white" : "bg-white border"}`}
            >
              You
            </button>
            <button
              onClick={() => setCurrentUser("userB")}
              className={`px-3 py-1 rounded ${currentUser === "userB" ? "bg-pink-600 text-white" : "bg-white border"}`}
            >
              Partner
            </button>
          </div>

          <div className="ml-auto flex gap-2">
            <button onClick={exportJSON} className="px-3 py-1 border rounded">Export</button>
            <label className="px-3 py-1 border rounded bg-white cursor-pointer">
              Import
              <input type="file" accept="application/json" onChange={importJSON} className="hidden" />
            </label>
            <button onClick={resetAll} className="px-3 py-1 border rounded bg-red-50">Reset</button>
          </div>
        </section>

        <main className="grid grid-cols-1 md:grid-cols-2 gap-6">
          <div>
            <div className="relative h-96 bg-white rounded shadow p-4 flex items-center justify-center overflow-hidden">
              {remaining.length === 0 ? (
                <div>No more movies. Add some!</div>
              ) : (
                remaining.map((movie, idx) => {
                  const depth = remaining.length - idx;
                  const isTop = idx === 0;
                  return (
                    <div key={movie.id} style={{ position: "absolute", zIndex: depth, transform: `scale(${1 - idx * 0.02}) translateY(${idx * 8}px)`, transition: "transform 200ms" }}>
                      <SwipeCard
                        id={movie.id}
                        onSwipe={(dir) => onCardSwiped(dir, movie.id)}
                        onCardLeftScreen={() => {}}
                        disabled={!isTop}
                        className="absolute w-72 md:w-96 h-56 md:h-80 rounded-lg shadow-lg overflow-hidden bg-gray-100 flex flex-col"
                        style={{ touchAction: "none" }}
                      >
                        <div style={{ width: 384, maxWidth: "80vw" }}>
                          {movie.poster ? (
                            <img src={movie.poster} alt={movie.title} className="object-cover h-2/3 w-full" onError={(e) => { e.currentTarget.style.display = 'none'; }} />
                          ) : (
                            <div className="h-2/3 w-full flex items-center justify-center text-gray-500">No poster</div>
                          )}
                          <div className="p-3 flex items-center justify-between bg-white">
                            <div>
                              <div className="font-semibold">{movie.title}</div>
                              <div className="text-xs text-gray-500">ID: {movie.id}</div>
                              {votes[currentUser]?.includes(movie.id) && (
                                <div className="text-xs text-green-600">✓ You liked this</div>
                              )}
                            </div>
                            <div className="text-sm text-gray-500">Swipe → to like</div>
                          </div>
                        </div>
                      </SwipeCard>
                    </div>
                  );
                })
              )}
            </div>

            <div className="mt-3 bg-white p-3 rounded shadow flex items-center gap-2">
              <button onClick={() => programmaticSwipe("left")} className="px-4 py-2 border rounded">←</button>
              <button onClick={() => programmaticSwipe("right")} className="px-4 py-2 bg-green-600 text-white rounded">→</button>

              <form onSubmit={addMovie} className="ml-auto flex gap-2 flex-1">
                <input value={newTitle} onChange={(e) => setNewTitle(e.target.value)} placeholder="Movie title" className="flex-1 border p-2 rounded" />
                <input value={newPoster} onChange={(e) => setNewPoster(e.target.value)} placeholder="Poster URL (optional)" className="flex-1 border p-2 rounded" />
                <button className="px-4 py-2 bg-green-600 text-white rounded">Add</button>
              </form>
            </div>

            <div className="mt-3 bg-white p-3 rounded shadow">
              <div className="text-sm text-gray-600">Last action: {lastDirection || "—"}</div>
              <div className="mt-2 flex gap-2">
                <button onClick={() => setVotes({ ...votes, [currentUser]: [] })} className="px-3 py-1 border rounded">Clear my likes</button>
                <button onClick={() => alert(JSON.stringify(votes, null, 2))} className="px-3 py-1 border rounded">Show votes</button>
              </div>
            </div>
          </div>

          <aside>
            <div className="bg-white p-4 rounded shadow mb-4">
              <h2 className="font-semibold mb-2">Matches ({matches.length})</h2>
              {matches.length === 0 ? (
                <div className="text-sm text-gray-500">No matches yet — keep swiping!</div>
              ) : (
                <ul className="space-y-2">
                  {matches.map((m) => (
                    <li key={m.id} className="flex items-center gap-3">
                      {m.poster ? <img src={m.poster} alt="" className="w-12 h-16 object-cover rounded" onError={(e) => { e.currentTarget.style.display = 'none'; }} /> : <div className="w-12 h-16 bg-gray-100 rounded" />}
                      <div>
                        <div className="font-medium">{m.title}</div>
                        <div className="text-xs text-gray-500">Both liked</div>
                      </div>
                    </li>
                  ))}
                </ul>
              )}
            </div>

            <div className="bg-white p-4 rounded shadow mb-4">
              <h3 className="font-semibold mb-2">Quick stats</h3>
              <div className="text-sm text-gray-700">You liked: {(votes.userA || []).length}</div>
              <div className="text-sm text-gray-700">Partner liked: {(votes.userB || []).length}</div>
              <div className="text-sm text-gray-700">Total movies: {movies.length}</div>
            </div>

            <div className="bg-white p-4 rounded shadow">
              <h3 className="font-semibold mb-2">Movie list</h3>
              <div className="max-h-64 overflow-auto">
                <ul className="space-y-2 text-sm">
                  {movies.map((m) => (
                    <li key={m.id} className="flex items-center justify-between gap-2">
                      <div className="truncate">{m.title}</div>
                      <div className="flex gap-2">
                        <button onClick={() => setMovies((arr) => arr.filter(x => x.id !== m.id))} className="px-2 py-1 border rounded text-xs">Delete</button>
                        <button onClick={() => { const t = prompt('Edit title', m.title); if (t != null) { setMovies(ms => ms.map(mm => mm.id === m.id ? { ...mm, title: t } : mm)) } }} className="px-2 py-1 border rounded text-xs">Edit</button>
                      </div>
                    </li>
                  ))}
                </ul>
              </div>
            </div>
          </aside>
        </main>

        <footer className="mt-6 text-sm text-gray-500">Tip: Switch user to let your partner swipe — matches update when both like the same movie.</footer>
      </div>
    </div>
  );
}
